css:
.products-carousel__title {
    display: none !important;
}
.product__buy, .product-card__buy {

}
#product-actions 
.product__buy:after {
  content: "Não enviamos para MS, MT, norte e nordeste, desculpe.";
  padding: 6px;
  color: #fff;
  background: red;
  position: relative;
  top: .5rem;
  display: block
}

#product-actions .product__buy::before {
  content: "Enviamos em até 5 dias úteis";
  padding: 6px;
  color: #fff;
  background: mediumseagreen;
  position: relative;
  top: -0.5rem; /* Aumenta a distância para ficar abaixo da vermelha */
  display: block;
}

.product__discount {
display: none !important;
}
.product-card__prices .prices__installments span::after {
    content: "(sem juros)";
    margin-left: 0.2rem; /* Adjust spacing before 'sem juros' */
}
.prices__discount-label {
    visibility: hidden;
    position: relative;
}

.prices__discount-label::before {
    content: "via pix";
    display: block;
    color: #000; /* Cor do texto */
    margin-left: 0.2rem; /* Espaçamento antes do texto, se necessário */
    position: absolute;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%); /* Centraliza o texto horizontal e verticalmente */
    white-space: nowrap; /* Evita quebra de linha */
}
