css:
.products-carousel__title {
    display: none !important;
}
.product__buy, .product-card__buy {

}
#product-actions 
.product__buy:after {
  content: "Não enviamos para MS, MT, norte e nordeste, desculpe.";
  padding: 6px;
  color: #fff;
  background: red;
  position: relative;
  top: .5rem;
  display: block
}

#product-actions .product__buy::before {
  content: "Enviamos em até 5 dias úteis";
  padding: 6px;
  color: #fff;
  background: mediumseagreen;
  position: relative;
  top: -0.5rem; /* Aumenta a distância para ficar abaixo da vermelha */
  display: block;
}

.product__discount {
display: none !important;
}
.product-card__prices .prices__installments span::after {
    content: "(sem juros)";
    margin-left: 0.2rem; /* Adjust spacing before 'sem juros' */
}

.brands-carousel-container {
  width: 100%;
  padding: 1rem 0;
}

.brands-carousel-header {
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: 1rem;
}

.brands-carousel-controls {
  display: flex;
  gap: 0.5rem;
}

.carousel-control {
  background-color: white;
  border: 1px solid #e2e8f0;
}

.carousel-control:hover {
  background-color: #f7fafc;
}

.brands-carousel-wrapper {
  position: relative;
  width: 100%;
  overflow: hidden;
}

.brands-carousel {
  display: flex;
  gap: 1rem;
  overflow-x: auto;
  scroll-snap-type: x mandatory;
  scrollbar-width: none; /* Firefox */
  -ms-overflow-style: none; /* IE and Edge */
  padding: 1rem 0;
}

.brands-carousel::-webkit-scrollbar {
  display: none; /* Chrome, Safari and Opera */
}

.brand-item {
  scroll-snap-align: start;
  min-width: 150px;
  height: 80px;
  background-color: black; /* Black background as requested */
  color: white; /* White font as requested */
  border-radius: 0.5rem;
  display: flex;
  align-items: center;
  justify-content: center;
  padding: 0.5rem;
  transition: transform 0.3s ease;
}

.brand-item:hover {
  transform: translateY(-5px);
}

.brand-link {
  width: 100%;
  height: 100%;
  display: flex;
  align-items: center;
  justify-content: center;
  color: white; /* White text color for links */
  text-decoration: none;
}

.brand-logo {
  max-width: 80%;
  max-height: 60px;
  object-fit: contain;
}

.brand-name {
  font-weight: 600;
  text-align: center;
  word-break: break-word;
}

/* Hide specific brands as requested */
