css:
/* Hide products carousel title */
.products-carousel__title {
  display: none !important;
}

/* Styling for the product buy section */
.product__buy, .product-card__buy {
}

/* Product actions messages */
#product-actions .product__buy:after {
  content: "Não enviamos para MS, MT, norte e nordeste, desculpe.";
  padding: 6px;
  color: #fff;
  background: red;
  position: relative;
  top: 0.5rem;
  display: block;
}

#product-actions .product__buy::before {
  content: "Enviamos em até 5 dias úteis";
  padding: 6px;
  color: #fff;
  background: mediumseagreen;
  position: relative;
  top: -0.5rem; /* Adjusted spacing to stay below the red message */
  display: block;
}

/* Hide product discounts */
.product__discount {
  display: none !important;
}

/* Add 'sem juros' text after installment prices */
.product-card__prices .prices__installments span::after {
  content: "(sem juros)";
  margin-left: 0.2rem; /* Adjust spacing before 'sem juros' */
}

/* Brands carousel styling */
.brands-carousel {
  background-color: #000; /* Background color */
  color: #fff; /* Text color */
  padding: 20px 0;
}

.brands-carousel * {
  color: #fff; /* Set text color for all internal elements */
}

/* Navigation arrows */
.glide__arrow {
  background-color: #000; /* Black background */
  color: #fff; /* White icon color */
  border: none;
  border-radius: 50%;
  width: 40px;
  height: 40px;
  font-size: 20px;
  opacity: 0.85;
  display: flex;
  align-items: center;
  justify-content: center;
  transition: background 0.2s, color 0.2s;
}

/* Hover effect for navigation arrows */
.glide__arrow:hover {
  background-color: #fff; /* White background on hover */
  color: #000; /* Black icon on hover */
}

/* Arrow icons */
.glide__arrow i {
  color: inherit; /* Inherit parent color */
}

/* Exclude specific brands from the carousel */
.glide__slide.brands-carousel__item:has(a[href="/alternate"]),
.glide__slide.brands-carousel__item:has(a[href="/warehouse-goods-inc"]),
.glide__slide.brands-carousel__item:has(a[href="/bem-bolado"]),
.glide__slide.brands-carousel__item:has(a[href="/dr-dabber"]),
.glide__slide.brands-carousel__item:has(a[href="/open"]),
.glide__slide.brands-carousel__item:has(a[href="/samsung"]),
.glide__slide.brands-carousel__item:has(a[href="/santa-cruz"]),
.glide__slide.brands-carousel__item:has(a[href="/groove"]),
.glide__slide.brands-carousel__item:has(a[href="/planet-of-the-vapes"]),
.glide__slide.brands-carousel__item:has(a[href="/marca/aparelhos-ghost-vapes"]),
.glide__slide.brands-carousel__item:has(a[href="/marca/santa-cruz-dichavadores"]),
.glide__slide.brands-carousel__item:has(a[href="/marca/newvape"]),
.glide__slide.brands-carousel__item:has(a[href="/marca/vapir"]),
.glide__slide.brands-carousel__item:has(a[href="/marca/grenco-science"]),
.glide__slide.brands-carousel__item:has(a[href="/marca/aparelhos-dynavap"]),
.glide__slide.brands-carousel__item:has(a[href="/magic-flight"]),
.glide__slide.brands-carousel__item:has(a[href="/marca/flowermate"]) {
  display: none !important;
}
