css:
.products-carousel__title {
    display: none !important;
}
.product__buy, .product-card__buy {

}
#product-actions 
.product__buy:after {
  content: "Não enviamos para MS, MT, norte e nordeste, desculpe.";
  padding: 6px;
  color: #fff;
  background: red;
  position: relative;
  top: .5rem;
  display: block
}

#product-actions .product__buy::before {
  content: "Enviamos em até 5 dias úteis";
  padding: 6px;
  color: #fff;
  background: mediumseagreen;
  position: relative;
  top: -0.5rem; /* Aumenta a distância para ficar abaixo da vermelha */
  display: block;
}

.product__discount {
display: none !important;
}
.product-card__prices .prices__installments span::after {
    content: "(sem juros)";
    margin-left: 0.2rem; /* Adjust spacing before 'sem juros' */
}
.brands-carousel {
  background-color: #000; /* Cor de fundo */
  color: #fff;            /* Cor do texto */
  padding: 20px 0;
}

.brands-carousel * {
  color: #fff; /* Cor do texto para todos os elementos internos */
}

.glide__arrow {
  background-color: #000; /* Fundo preto */
  color: #fff;            /* Ícone branco */
  border: none;
  border-radius: 50%;
  width: 40px;
  height: 40px;
  font-size: 20px;
  opacity: 0.85;
  display: flex;
  align-items: center;
  justify-content: center;
  transition: background 0.2s, color 0.2s;
}

/* Hover nas setas */
.glide__arrow:hover {
  background-color: #fff; /* Fundo branco ao passar o mouse */
  color: #000;            /* Ícone preto ao passar o mouse */
}

/* Ícones dentro das setas */
.glide__arrow i {
  color: inherit; /* Herdar a cor do elemento pai */
}
