css:
.brands-carousel {
  /* Main container for the carousel */
  position: relative;
  z-index: 9;
  width: 100%;
  height: 60px; /* Match the header menu height */
  background-color: #000; /* Black background */
  display: flex;
  align-items: center; /* Center items vertically */
  justify-content: center; /* Center items horizontally */
  box-sizing: border-box;
  margin: 0;
  padding: 0;
}

.brands-carousel .container {
  /* Ensure the container spans the full width */
  width: 100%;
  display: flex;
  align-items: center;
  justify-content: center;
}

.brands-carousel .glide__arrow {
  /* Styling for navigation arrows */
  position: absolute;
  top: 50%;
  transform: translateY(-50%);
  background-color: #000;
  color: #fff; /* White arrow font */
  border: none;
  border-radius: 50%;
  width: 40px;
  height: 40px;
  font-size: 20px;
  display: flex;
  align-items: center;
  justify-content: center;
  transition: background 0.2s, color 0.2s;
  opacity: 0.85;
  cursor: pointer;
}

.brands-carousel .glide__arrow:hover {
  /* Hover effect for arrows */
  background-color: #fff;
  color: #000;
}

.brands-carousel .glide__arrow.glide__arrow--left {
  /* Left arrow positioning */
  left: 10px;
}

.brands-carousel .glide__arrow.glide__arrow--right {
  /* Right arrow positioning */
  right: 10px;
}

.brands-carousel .glide__slide {
  /* Carousel slide */
  display: flex;
  justify-content: center;
  align-items: center;
  padding: 10px;
  min-width: 100px; /* Set a minimum width for the slides */
}

.brands-carousel .glide__slide img {
  /* Image inside the carousel slide */
  max-width: 100%;
  max-height: 40px; /* Ensure images fit within the header menu height */
  object-fit: contain;
}

.brands-carousel .glide__slide a {
  /* Links inside the slides */
  color: #fff; /* White font color */
  font-size: 14px;
  text-align: center;
  text-decoration: none;
}

.brands-carousel .glide__slide a:hover {
  /* Hover effect for links */
  text-decoration: underline;
}

/* Exclude specific brands from being displayed */
.brands-carousel .glide__slide:has(a[href="/alternate"]),
.brands-carousel .glide__slide:has(a[href="/warehouse-goods-inc"]),
.brands-carousel .glide__slide:has(a[href="/bem-bolado"]),
.brands-carousel .glide__slide:has(a[href="/dr-dabber"]),
.brands-carousel .glide__slide:has(a[href="/open"]),
.brands-carousel .glide__slide:has(a[href="/samsung"]),
.brands-carousel .glide__slide:has(a[href="/santa-cruz"]),
.brands-carousel .glide__slide:has(a[href="/groove"]),
.brands-carousel .glide__slide:has(a[href="/planet-of-the-vapes"]),
.brands-carousel .glide__slide:has(a[href="/marca/aparelhos-ghost-vapes"]),
.brands-carousel .glide__slide:has(a[href="/marca/santa-cruz-dichavadores"]),
.brands-carousel .glide__slide:has(a[href="/marca/newvape"]),
.brands-carousel .glide__slide:has(a[href="/marca/vapir"]),
.brands-carousel .glide__slide:has(a[href="/marca/grenco-science"]),
.brands-carousel .glide__slide:has(a[href="/marca/aparelhos-dynavap"]),
.brands-carousel .glide__slide:has(a[href="/magic-flight"]),
.brands-carousel .glide__slide:has(a[href="/marca/flowermate"]) {
  display: none !important;
}
