/* Updated Styling for Centering the Carousel */
html, body {
  margin: 0;
  padding: 0;
  height: 100%;
  display: flex;
  justify-content: center;
  align-items: center;
  background-color: #f4f4f4; /* Light background for contrast */
  overflow: hidden;
  box-sizing: border-box;
}

.brands-carousel {
  position: relative;
  z-index: 9;
  width: 100%;
  max-width: 80%; /* Adjust width as needed */
  height: 60px; /* Match header menu height */
  background-color: #000;
  display: flex;
  align-items: center; /* Vertically center items */
  justify-content: center; /* Horizontally center items */
  overflow: hidden;
  box-sizing: border-box;
  border-radius: 8px; /* Rounded corners for a modern look */
}

.brands-carousel .container {
  width: 100%;
  display: flex;
  align-items: center;
  justify-content: center;
  margin: 0;
  padding: 0;
}

.brands-carousel .glide__track {
  display: flex;
  justify-content: center;
  align-items: center;
  width: 100%;
  overflow: hidden;
}

.brands-carousel .glide__slides {
  display: flex;
  justify-content: flex-start;
  align-items: center;
  width: 100%; /* Ensure slides take up full width */
  margin: 0;
  padding: 0;
}

.brands-carousel .glide__slide {
  flex: 0 0 auto; /* Prevent shrinking */
  padding: 10px;
}

.brands-carousel .glide__slide img {
  max-width: 100%;
  max-height: 50px; /* Fit within carousel height */
  object-fit: contain;
}

.brands-carousel .glide__slide a {
  color: #fff;
  font-size: 14px;
  text-align: center;
  text-decoration: none;
}

.brands-carousel .glide__slide a:hover {
  text-decoration: underline;
}

/* Styling for navigation arrows */
.brands-carousel .glide__arrow {
  position: absolute;
  top: 50%;
  transform: translateY(-50%);
  background-color: #000;
  color: #fff;
  border: none;
  border-radius: 50%;
  width: 40px;
  height: 40px;
  font-size: 20px;
  display: flex;
  align-items: center;
  justify-content: center;
  transition: background 0.2s, color 0.2s;
  opacity: 0.85;
  cursor: pointer;
  z-index: 10;
}

.brands-carousel .glide__arrow:hover {
  background-color: #fff;
  color: #000;
}

.brands-carousel .glide__arrow.glide__arrow--left {
  left: 10px;
}

.brands-carousel .glide__arrow.glide__arrow--right {
  right: 10px;
}

/* Responsive Adjustments */
@media (max-width: 1200px) {
  .brands-carousel {
    height: 50px; /* Adjust carousel height for medium screens */
  }
  .brands-carousel .glide__arrow {
    width: 35px;
    height: 35px;
    font-size: 18px;
  }
  .brands-carousel .glide__slide img {
    max-height: 40px; /* Scale images down */
  }
}

@media (max-width: 768px) {
  .brands-carousel {
    height: 40px; /* Reduce carousel height further for smaller screens */
  }
  .brands-carousel .glide__arrow {
    display: none; /* Hide arrows for small screens */
  }
  .brands-carousel .glide__slide img {
    max-height: 30px;
  }
}

@media (max-width: 480px) {
  .brands-carousel {
    height: 30px; /* Adjust height for very small screens */
  }
  .brands-carousel .glide__slide {
    padding: 5px; /* Reduce padding */
  }
  .brands-carousel .glide__slide img {
    max-height: 25px; /* Scale images further for very small screens */
  }
}
