.product__buy, .product-card__buy {

}
#product-actions 
.product__buy:after {
  content: "Não enviamos para MS, MT, norte e nordeste, desculpe.";
  padding: 6px;
  color: #fff;
  background: red;
  position: relative;
  top: .5rem;
  display: block
}

#product-actions .product__buy::before {
  content: "Cupom VAPOR10 à partir de R$500, VAPOR5 à partir de R$100";
  padding: 6px;
  color: #fff;
  background: mediumseagreen;
  position: relative;
  top: -0.5rem; /* Aumenta a distância para ficar abaixo da vermelha */
  display: block;
}

#product-actions .product__buy::before {
  content: "Enviamos em até 5 dias úteis";
  padding: 6px;
  color: #fff;
  background: mediumseagreen;
  position: relative;
  top: -0.5rem; /* Aumenta a distância para ficar abaixo da vermelha */
  display: block;
}

.product__discount {
display: none !important;
}
.product-card__prices .prices__installments span::after {
    content: "(sem juros)";
    margin-left: 0.2rem; /* Adjust spacing before 'sem juros' */
}
.remove_newsletter_check_box {
#account-form-marketing,
label[for="account-form-marketing"] {
display: none !important;
}
