css:
.brands-carousel {
  /* Main container for the carousel */
  position: relative;
  z-index: 9;
  width: 100%; /* Full width */
  height: 60px; /* Match header menu height */
  background-color: #000; /* Black background */
  display: flex;
  align-items: center; /* Vertically center items */
  justify-content: center; /* Horizontally center items */
  overflow: hidden; /* Prevent extra scrolling/space */
  margin: 0;
  padding: 0;
  box-sizing: border-box;
}

.brands-carousel .container {
  /* Ensure the container spans the full width */
  width: 100%;
  max-width: 100%; /* Prevent horizontal scroll */
  margin: 0 auto; /* Center container */
  display: flex;
  align-items: center;
  justify-content: center; /* Center items within the carousel */
  overflow: hidden;
}

.brands-carousel .glide__track {
  /* Track for slides */
  display: flex;
  justify-content: center;
  align-items: center;
  overflow: hidden;
}

.brands-carousel .glide__slides {
  /* Slides container */
  display: flex;
  justify-content: space-between;
  margin: 0 auto; /* Center slides */
  overflow: hidden;
}

.brands-carousel .glide__slide {
  /* Individual slides */
  flex: 0 0 auto;
  width: auto; /* Auto width for proper centering */
  padding: 10px;
}

.brands-carousel .glide__slide img {
  /* Images inside the carousel slide */
  max-width: 100%;
  max-height: 50px; /* Fit images within the carousel height */
  object-fit: contain;
}

.brands-carousel .glide__slide a {
  /* Links inside the slides */
  color: #fff; /* White font color */
  font-size: 14px;
  text-align: center;
  text-decoration: none;
}

.brands-carousel .glide__slide a:hover {
  /* Hover effect for links */
  text-decoration: underline;
}

/* Styling for navigation arrows */
.brands-carousel .glide__arrow {
  position: absolute;
  top: 50%;
  transform: translateY(-50%);
  background-color: #000;
  color: #fff; /* White arrow font */
  border: none;
  border-radius: 50%;
  width: 40px;
  height: 40px;
  font-size: 20px;
  display: flex;
  align-items: center;
  justify-content: center;
  transition: background 0.2s, color 0.2s;
  opacity: 0.85;
  cursor: pointer;
}

.brands-carousel .glide__arrow:hover {
  /* Hover effect for arrows */
  background-color: #fff;
  color: #000;
}

.brands-carousel .glide__arrow.glide__arrow--left {
  /* Left arrow positioning */
  left: 10px;
}

.brands-carousel .glide__arrow.glide__arrow--right {
  /* Right arrow positioning */
  right: 10px;
}

/* Exclude specific brands from being displayed */
.brands-carousel .glide__slide:has(a[href="/alternate"]),
.brands-carousel .glide__slide:has(a[href="/warehouse-goods-inc"]),
.brands-carousel .glide__slide:has(a[href="/bem-bolado"]),
.brands-carousel .glide__slide:has(a[href="/dr-dabber"]),
.brands-carousel .glide__slide:has(a[href="/open"]),
.brands-carousel .glide__slide:has(a[href="/samsung"]),
.brands-carousel .glide__slide:has(a[href="/santa-cruz"]),
.brands-carousel .glide__slide:has(a[href="/groove"]),
.brands-carousel .glide__slide:has(a[href="/planet-of-the-vapes"]),
.brands-carousel .glide__slide:has(a[href="/marca/aparelhos-ghost-vapes"]),
.brands-carousel .glide__slide:has(a[href="/marca/santa-cruz-dichavadores"]),
.brands-carousel .glide__slide:has(a[href="/marca/newvape"]),
.brands-carousel .glide__slide:has(a[href="/marca/vapir"]),
.brands-carousel .glide__slide:has(a[href="/marca/grenco-science"]),
.brands-carousel .glide__slide:has(a[href="/marca/aparelhos-dynavap"]),
.brands-carousel .glide__slide:has(a[href="/magic-flight"]),
.brands-carousel .glide__slide:has(a[href="/marca/flowermate"]) {
  display: none;
  width: 0; /* Remove space for hidden slides */
  padding: 0;
  margin: 0;
}

/* Responsive Adjustments */
@media (max-width: 768px) {
  .brands-carousel {
    height: 50px; /* Reduce height for smaller devices */
  }
  .brands-carousel .glide__arrow {
    width: 30px;
    height: 30px;
    font-size: 16px;
  }
  .brands-carousel .glide__slide img {
    max-height: 40px; /* Scale images for smaller screens */
  }
}

@media (max-width: 480px) {
  .brands-carousel {
    height: 40px; /* Further reduce height for mobile */
  }
  .brands-carousel .glide__arrow {
    display: none; /* Hide arrows on very small screens */
  }
  .brands-carousel .glide__slide img {
    max-height: 30px; /* Scale images for very small screens */
  }
}
